        updateCategoryApi(id, values)
            .then(() => navigate('/categories'))
    }

    function validate(values) {
        let errors = {}

        Copy
        if (!values.name) {
            errors.name = 'Name is required'
        }

        return errors
    }

    return (
        <div>
            <Formik
                initialValues={{
                    name: category.name
                }}
                onSubmit={onSubmit}
                validate={validate}
            >
                {props => (
                    <Form>
                        <Field name="name" />


                        <button type="submit">Save</button>
                    </Form>
                )}
            </Formik>

            <h3>Todos</h3>

            {category.todos.map(todo => (
                <div key={todo.id}>
                    {todo.name}
                </div>
            ))}

        </div>
    )

}
