import { useEffect, useState } from "react";
import { deleteTodoApi, retrieveAllTodosForUsernameApi } from "./api/TodoApiService";

function ListTodosComponent() {
    const today = new Date();
    const targetDate = new Date(today.getFullYear() + 12, today.getMonth(), today.getDay());

    const [todos, setTodos] = useState([])
    const [message, setMessage] = useState(null)
    useEffect(() => refreshTodos(), [])

    function refreshTodos() {
        retrieveAllTodosForUsernameApi('vick7')
            .then(response => {
                setTodos(response.data)
            })
            .catch(error => console.log(error))

    }
    function deleteTodo(id) {
        deleteTodoApi('vick7', id)
            .then(
                () => {
                    setMessage(`Deletion of todo with ID = ${id} was successful!`)
                    refreshTodos()
                }

            //1: Display message
            //2: Update Todos list
        )

    }


    return (
        <div className="container">
            <h1>Things You Want To Do! </h1>
            <div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Is Done?</th>
                            <th>Target Date</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            todos.map(
                                todo => (
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.done.toString()}</td>
                                        {/* <td>{todo.targetDate.toDateString()}</td> */}
                                        <td>{todo.targetDate.toString()}</td>
                                        <td><button className="btn btn-warning" onClick={() => deleteTodo(todo.id)} style={{ display: 'inline-block', width: 'auto' }}>Delete</button></td>
                                    </tr>
                                )
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default ListTodosComponent
