import { useEffect, useState } from "react";
import { getUserIn4Api} from "./api/UserApiService";
import { useNavigate } from "react-router-dom";

function UserProfileComponent() {
    const[userDto, setUserDto] = useState()
    const navigate = useNavigate();
    useEffect(() => refreshTodos(), [])

    function refreshTodos() {
        getUserIn4Api()
            .then(response => {
                setUserDto(response.data)
            })
            .catch(error => console.log(error))

    }


    return (
        <div class="container d-flex justify-content-center align-items-center">
             
        <div class="card">

         <div class="upper">

           <img src="https://i.imgur.com/Qtrsrk5.jpg" class="img-fluid"/>
           
         </div>

         <div class="user text-center">

           <div class="profile">

             <img src="https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg" class="rounded-circle" width="80"/>
             
           </div>

         </div>


         <div class="mt-5 text-center">

           <h4 class="mb-0">{{username}}</h4>
           <span class="text-muted d-block mb-2">{{email}}</span>

           <button class="btn btn-dark btn-sm follow">Change Password</button>


           
         </div>
          
        </div>

      </div>
    )
}

export default UserProfileComponent
