import { useState } from 'react'
import { executeRegistrationService } from './api/AuthenticationApiService'
import './RegisterComponent.css';

function RegisterComponent() {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [email, setEmail] = useState('')
  const [showSuccessMessage, setShowSuccessMessage] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  
  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handlePasswordChange(event) {
    setPassword(event.target.value);
  }

  function handleEmailChange(event) {
    setEmail(event.target.value);
  }

 async function handleSubmit(e) {
   e.preventDefault();

   // Validate email
   if (!/\S+@\S+\.\S+/.test(email)) {
     alert('Invalid email format.');
     return;
   }
   
   try{
     await executeRegistrationService({ username, password,email })
     setShowSuccessMessage(true)
   }
   catch(error){
     console.error("Error during registration", error);

    // Check if the error response has a message and status code is CONFLICT (409)
    if (error.response && error.response.status === 409 && error.response.data.message) {
      setErrorMessage(error.response.data.message);
    } else {
      setErrorMessage('An unknown error occurred.');
    }
     
     
   }
 }

 return (
  <div className="Register">
    <h1>Time to register!</h1>

    {showSuccessMessage && (
      <div className="successMessage">
        Registration Successful. Please check your email to activate your account.
      </div>
    )}

    {errorMessage && ( 
      <div className="errorMessage">
        {errorMessage}
      </div>
    )}

    <div className="LoginForm">
      <div className="form-group">
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={email}
          onChange={handleEmailChange}
        />
      </div>

      <div className="form-group">
        <button 
          type="submit" 
          className="btn btn-dark register-button"
          onClick={handleSubmit}
        >
          Register
        </button>
      </div>
    </div>
  </div>
);
}

export default RegisterComponent;
