import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from './security/AuthContext'
import { Formik, Form, Field, ErrorMessage } from 'formik'

import {
    retrieveCategoryApi,
    updateCategoryApi,
    createCategoryApi,
    retrieveAllTodoForCategory
} from './api/CategoryApiService';

export default function CategoryComponent() {

    const { id } = useParams();
    const [name, setName] = useState('');
    const [todos, setTodos] = useState([]);
    const navigate = useNavigate();
    const authContext = useAuth();
    const username = authContext.username;

    useEffect(() => {
        if (id) {
            getCategory();
            getTodos();
        }
    }, [id]);

    const getCategory = () => {
        retrieveCategoryApi(id)
            .then(response => setName(response.data.name));
    }

    const getTodos = () => {
        retrieveAllTodoForCategory(id)
            .then(response => setTodos(response.data));
    }

    const onSubmit = (values) => {
        const category = {
            id,
            name: values.name,
        }

        if (id = -1) {
            createCategoryApi(category)
                .then(response => {
                    navigate('/category')
                })
                .catch(error => console.log(error))
            
        } else {
            updateCategoryApi(id, category)
                .then(response => {
                    navigate('/category')
                })
                .catch(error => console.log(error))
        }
    }

    return (
        <Formik initialValues={{ name }}
            enableReinitialize={true}
            onSubmit={onSubmit} >
            {
                        (props) => (
                            <Form>
                                
                                <fieldset className="form-group">
                                    <label>Description</label>
                                    <Field type="text" className="form-control" name="name" />
                                </fieldset>
                                
                                <div className="text-center">
                                    <button className="btn btn-dark custom-button m-5" type="submit">Save</button>
                                </div>
                            </Form>
                        )
                    }
        </Formik>
    )
}
