import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from './security/AuthContext'
function LoginComponent() {

    const [username, setUsername] = useState('Vick7')
    const [password, setPassword] = useState('')
    const [showSuccessMessage, setShowSuccessMessage] = useState(false)
    const [showErrorMessage, setShowErrorMessage] = useState(false)
    const navigate = useNavigate();
    const authContext = useAuth();


    function handleUsernameChange(event) {
        setUsername(event.target.value);
    }
    function handlePasswordChange(event) {
        setPassword(event.target.value);
    }

    function handleSubmit() {
        if (authContext.login(username, password)) {
            navigate(`/welcome/${username}`)
        }
        else {
            setShowErrorMessage(true)
        }
    }

    return (
        <div className="Login">
            <h1>Time to login!</h1>
            {showSuccessMessage && <div className="successMessage">Authenticated Successfully</div>}
            {showErrorMessage && <div className="errorMessage">Authentication Failed. Please check your credentials.</div>}
            <div className="LoginForm">
                <div class="form-outline mb-4">
                    {/* <!-- Email input -->
                    <div class="form-outline mb-4">
                        <input type="email" id="form2Example1" class="form-control" />
                        <label class="form-label" for="form2Example1">Email address</label>
                    </div>

                    <!-- Password input -->
                    <div class="form-outline mb-4">
                        <input type="password" id="form2Example2" class="form-control" />
                        <label class="form-label" for="form2Example2">Password</label>
                    </div> */}
                    
                    <input type="text" name="username" id="form2Example1" value={username} onChange={handleUsernameChange} />
                    <label class="form-label" for="form2Example1">User Name:</label>
                </div>

                <div class="form-outline mb-4">
                    
                    <input type="password" id="form2Example2" name="password" value={password} onChange={handlePasswordChange} />
                    <label class="form-label" for="form2Example2">Password:</label>
                </div>
                <div>
                    <button type="button" name="login" class="btn btn-primary btn-block mb-4" onClick={handleSubmit}>login</button>
                </div>

            </div>
        </div>
    )
}

export default LoginComponent
