import { useEffect, useState } from "react";
import { getUserIn4Api } from "./api/UserApiService";
import { useAuth } from "./security/AuthContext";
import { useNavigate } from "react-router-dom";

function UserProfileComponent() {

    const [todos, setTodos] = useState([])
    const [message, setMessage] = useState(null)
    const navigate = useNavigate();
    useEffect(() => refreshTodos(), [])

    function refresh() {
        getUserIn4Api()
            .then(response => {
                setTodos(response.data)
            })
            .catch(error => console.log(error))

    }
    function deleteTodo(id) {
        deleteTodoApi(id)
            .then(
                () => {
                    setMessage(`Deletion of todo with ID = ${id} was successful!`)
                    refreshTodos()
                }

                //1: Display message
                //2: Update Todos list
            )
            .catch(error => console.log(error))

    }

    function updateTodo(id) {
        navigate(`/todo/${id}`)

    }
    function addNewTodo(){
        navigate('/todo/-1')

    }
    function noteTodo(id){
        navigate(`/todo/${id}/note`)
    }


    return (
        <div className="container">
            <h1>Things You Want To Do! </h1>
            {message && <div className="alert alert-warning">{message}</div>}
            
            <button type="button" className="btn btn-secondary btn-lg custom-button m-5" onClick={addNewTodo} >Add New Todo</button>
        </div>
    )
}

export default ListTodosComponent
